INSERT INTO `gbd_icl_string_translations` VALUES (7606,7614,'ru',0,NULL,'Перевод строк WPML включен, но не задействован. Сначала завершите установку WPML.',NULL,'',0,'2017-11-06 11:02:49'),(7607,7615,'ru',0,NULL,'Перевод WPML',NULL,'',0,'2017-11-06 11:02:49'),(7608,7616,'ru',0,NULL,'Обновление WPML не завершено',NULL,'',0,'2017-11-06 11:02:49'),(7609,7617,'ru',0,NULL,'WPML предусматривает автоматическую регистрацию строк на перевод. Это позволяет переводить создаваемый пользователем контент при минимальном применении PHP-кода.',NULL,'',0,'2017-11-06 11:02:49'),(7610,7618,'ru',0,NULL,'WPML может отслеживать, где на страницах в общем доступе используются строки. Включение этой функции активирует возможность «просмотр на странице» и облегчает перевод.',NULL,'',0,'2017-11-06 11:02:49'),(7611,7619,'ru',0,NULL,'WPML необходимо обновить базу данных. Это обновление позволит повысить производительность WPML при получении переведенных строк.',NULL,'',0,'2017-11-06 11:02:49'),(7612,7620,'ru',0,NULL,'Учетная запись WPML.org',NULL,'',0,'2017-11-06 11:02:49'),(7613,7621,'ru',0,NULL,'Ожидание переводчика',NULL,'',0,'2017-11-06 11:02:49'),(7614,7622,'ru',0,NULL,'Виджет уже преобразован',NULL,'',0,'2017-11-06 11:02:49'),(7615,7623,'ru',0,NULL,'Да – полностью отключить эти уведомления',NULL,'',0,'2017-11-06 11:02:49'),(7616,7624,'ru',0,NULL,'У вас обновленные версии %s и %s, но эти компоненты не были обновлены:',NULL,'',0,'2017-11-06 11:02:49'),(7617,7625,'ru',0,NULL,'У вас обновленная версия %s, но этот компонент не был обновлен:',NULL,'',0,'2017-11-06 11:02:49'),(7618,7626,'ru',0,NULL,'Эту строку нельзя перевести, поскольку она не была направлена на перевод на этот язык',NULL,'',0,'2017-11-06 11:02:49'),(7619,7627,'ru',0,NULL,'Эту строку нельзя перевести, поскольку она была назначена другому переводчику',NULL,'',0,'2017-11-06 11:02:49'),(7620,7628,'ru',0,NULL,'Нельзя добавить пакеты на этом языке к корзине, поскольку она уже содержит записи, пакеты или строки другого исходного язык